import React, { createContext, useEffect, useState } from "react";
import { jwtDecode } from "jwt-decode";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const AuthContext = createContext();

const useAuth = () => {
  const [authTokens, setAuthTokens] = useState(null);
  const [user, setUser] = useState(null);
  const [isAdmin, setIsAdmin] = useState(false);
  const [loading, setLoading] = useState(true)

  const navigate = useNavigate();
  const loginUser = async (username, password) => {
    setLoading(true);
    try {
        const response = await axios.post('http://127.0.0.1:8000/api/authentication/login', {
            username: username,
            password: password
        });

        const data = response.data;

        if (response.status === 200) {
            setAuthTokens(data);
            setUser(jwtDecode(data.access));
            localStorage.setItem('authTokens', JSON.stringify(data));
            navigate('/');
        } else {
            alert('Something went wrong!');
        }
    } catch (error) {
        console.error('Error occurred during login:', error);
        alert('Failed to authenticate. Please try again.');
    } finally {
      setLoading(false);
    }
};

  const logoutUser = () => {
    setAuthTokens(null);
    setUser(null);
    setIsAdmin(false);
    localStorage.removeItem("authTokens");
    navigate("/login");
  };

  // const updateToken = async () => {
  //   console.log("Update token called");
  //   try {
  //     const response = await fetch(
  //       "http://127.0.0.1:8000/api/authentication/refresh",
  //       {
  //         method: "POST",
  //         headers: {
  //           "Content-Type": "application/json",
  //         },
  //         body: JSON.stringify({
  //           refresh: authTokens.refresh_token,
  //         }),
  //       }
  //     );
  //     if (response.ok) {
  //       const data = await response.json();
  //       setAuthTokens(data);
  //       setUser(jwtDecode(data.access_token));
  //       localStorage.setItem("authTokens", JSON.stringify(data));
  //     } else {
  //       throw new Error("Failed to refresh token");
  //     }
  //   } catch (error) {
  //     console.error("Error occurred during token refresh:", error);
  //     logoutUser();
  //   }
  // };

  useEffect(() => {

    if(authTokens){
      setUser(jwtDecode(authTokens.access_token))
    }  
     setLoading(false)
}, [authTokens]);

return { user,isAdmin, loginUser, logoutUser };
};


const AuthProvider = ({ children }) => {
  const auth = useAuth();
  return <AuthContext.Provider value={auth}>{children}</AuthContext.Provider>;
};

export { AuthContext, AuthProvider };

